import fs from 'node:fs';
import { toTypeScriptNotation } from '../lib/schema-typescript.js';
import * as types from '../schema.js';

const out = fs.createWriteStream('schema-types.d.ts');

out.write(`\
/**
 * Generated by scripts/gen-whole-world-ts.js:
 */

`);

for (const [name, type] of Object.entries(types)) {
  out.write(
    `export type ${name} = ${toTypeScriptNotation(type)};\n\n`,
  );
}
